import argparseimport osfrom core.io.audio_loader import load_audio, save_wavfrom core.hybrid.pipeline import process_audiodef main():    parser = argparse.ArgumentParser(description="Digital Reverse Engine CLI")    parser.add_argument(        "input",        help="Path to input audio file (WAV, MP3, FLAC, OGG, M4A)"    )    parser.add_argument(    "--mode",    default="TRUE_REVERSE",    choices=[        "TRUE_REVERSE",        "GRAIN_REVERSE",        "TRANSIENTAWARE_REVERSE",        "HQ_REVERSE",        "DJ_REVERSE",        "STUDIO_REVERSE",    ],    help="DSP reverse mode")    parser.add_argument(        "--tier",        default="free",        choices=["free", "trial", "premium", "enterprise"],        help="Economic tier for gating"    )    parser.add_argument(        "--output",        default=None,        help="Optional output WAV file path"    )    args = parser.parse_args()    # Load audio (supports WAV, MP3, FLAC, OGG, M4A)    print(f"\nLoading audio: {args.input}")    audio, sr = load_audio(args.input)    print("DEBUG: Loaded audio shape:", audio.shape)    print("DEBUG: Sample rate:", sr)    # Metadata placeholder    enriched_metadata = {        "contribution_type": "cli_user",        "complexity_factor": 1.0,        "transient_density": 0.2,        "quality_proxy_score": 1.0    }    # Run the hybrid pipeline    print(f"Running mode: {args.mode}")    processed, meta, receipt = process_audio(        audio,        sample_rate=sr,        mode=args.mode,        tier=args.tier,        enriched_metadata=enriched_metadata    )    print("DEBUG: Processed audio shape:", processed.shape)    print("DEBUG: Processed min/max:", processed.min(), processed.max())    # Determine output path    if args.output:        output_path = args.output    else:        base = os.path.splitext(args.input)[0]        output_path = f"{base}_{args.mode}.wav"    print("DEBUG: Attempting to save to:", output_path)    # Save output WAV    save_wav(output_path, processed, sr)    # Print summary    print("\n=== DONE ===")    print("Output saved to:", output_path)    print("Mode:", args.mode)    print("Tier:", args.tier)    print("Cost:", receipt["datacostunits"])    print("Gating:", receipt["gating"])    print("Signature:", receipt["signature"][:12])    print()if __name__ == "__main__":    main()